import React from 'react'
import { Paper } from '@material-ui/core'
import { Link } from "react-router-dom"

import "./sign_up.styles.css"

const SignUp = () => {

    const initialState = {
        username: '',
        email: '',
        pw: '',
        confirmPw: '',
    }

    const [userInfo, setUserInfo] = React.useState(initialState)

    const { username, email, pw, confirmPw } = userInfo

    const handleChange = e => {
        e.preventDefault()
        setUserInfo({...userInfo, [e.target.name]: e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (pw !== confirmPw) {
            alert("Passwords do not match!")
            setUserInfo({
                ...userInfo,
                pw: '',
                confirmPw: ''
            })
        } else {
            setUserInfo({
                username: '',
                email: '',
                pw: '',
                confirmPw: ''
            })
        }
        console.log(userInfo)
    }

    return (
        <Paper className="signup-container" elevation={3}>
            <h2 className="signup-header">SIGN UP</h2>
            <form onSubmit={handleSubmit}>
                <label className="signup-label">Username</label>
                <input 
                    className="signup-input"
                    name="username"
                    value={username}
                    type="text"
                    placeholder="username"
                    onChange={handleChange}
                />

                <label className="signup-label">Email</label>
                <input 
                    className="signup-input"
                    name="email"
                    value={email}
                    type="email"
                    placeholder="email"
                    onChange={handleChange}
                />

                <label className="signup-label">Password</label>
                <input 
                    className="signup-input"
                    name="pw"
                    value={pw}
                    type="password"
                    placeholder="password"
                    onChange={handleChange}
                />

                <label className="signup-label">Confirm Password</label>
                <input 
                    className="signup-input"
                    name="confirmPw"
                    value={confirmPw}
                    type="password"
                    placeholder="Confirm Password"
                    onChange={handleChange}
                />

                <input 
                    className="signup-submit"
                    type='submit'
                    onSubmit={handleSubmit}
                />
                <Link className="link-to-login" to="/login">Already registered? Click here to log in</Link>
            </form>
        </Paper>
    )
}

export default SignUp
