import React from 'react'
import { Paper } from '@material-ui/core';

import "./create-form.styles.css"

const CreateNewProjectForm = () => {
    const initialState = {
        name: '',
        tech_stack: '',
        curCap: 1,
        maxCap: null,
        users: []
    }

    const [projectInfo, setProjectInfo] = React.useState(initialState)

    const { username, tech_stack, curCap, maxCap, users } = projectInfo

    const handleChange = e => {
        e.preventDefault()
        setProjectInfo({...projectInfo, [e.target.name]: e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault()
        setProjectInfo({
            username: '',
            email: '',
            pw: '',
            confirmPw: ''
        })
        console.log(projectInfo)
    }

    return (
        <Paper className="new-project-container" elevation={3}>
            <h2 className="new-project-header">Create a New Project</h2>
            <form onSubmit={handleSubmit}>
                <label className="new-project-label">Project Name</label>
                <input 
                    className="new-project-input"
                    name="username"
                    value={username}
                    type="text"
                    placeholder="username"
                    onChange={handleChange}
                />

                <label className="new-project-label">Tech Stack</label>
                <input 
                    className="new-project-input"
                    name="tech_stack"
                    value={tech_stack}
                    type="text"
                    placeholder="tech stack"
                    onChange={handleChange}
                />

                <label className="new-project-label">Maximum Capacity</label>
                <input 
                    className="new-project-input"
                    name=""
                    value={maxCap}
                    type="number"
                    placeholder="number from two to six"
                    onChange={handleChange}
                />
                <input 
                    className="new-project-submit"
                    type='submit'
                    onSubmit={handleSubmit}
                />
            </form>
        </Paper>
    )
}

export default CreateNewProjectForm
